import React, { useState, useRef } from 'react';
import {useNavigate} from "react-router-dom";
import { useSelector, useDispatch } from 'react-redux';
import {
  addTask
} from './app/taskSlice';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import 'react-datepicker/dist/react-datepicker-cssmodules.css';


function NewTask() {

  const navigate = useNavigate();
  const dispatch=useDispatch();

  const [startTaskDate, setStartTaskDate] = useState(new Date());
  const [endTaskDate, setEndTaskDate] = useState(new Date());

   /*Переход на страницу Список задач по клику */
  function goToApp(){
    navigate(-1)
  }
  
  /*добавление новой задачи */
  function addNewTask() {
     let textHead=document.getElementById('taskHeader').value;
     let textBody=document.getElementById('taskText').value;
     
     const objNewTask = {
      'header':textHead,
      'text': textBody
     }

     dispatch(addTask(objNewTask));
    }

  
  
    return (
      <div className="App">
      <header className="App-header">
       <div className='mainDiv'>
        <button className='goToButton marginNewTask' onClick={goToApp}>Назад к списку задач</button>
        <textarea id='taskHeader' className='TitleTaskTextarea marginNewTask' placeholder='Название задачи'></textarea>
        <textarea id='taskText' className='TextTaskTextarea marginNewTask' placeholder='Текст Задачи'></textarea>
        <div className='dateDiv addNewTask'>
          <div className='fromDate'>
            <DatePicker selected={startTaskDate} onChange={(date) => setStartTaskDate(date)} />  
          </div>
          <div className='toDate'>
           
          </div>
        </div>
        <button className='addNewTask' onClick={addNewTask}>Добавить Задачу</button>
       </div>
      </header>
    </div>
    )
  }
  
  export default NewTask;
