import React, { useState, useEffect } from 'react';
import {useNavigate} from "react-router-dom";
import { useSelector, useDispatch } from 'react-redux';
import {  addTask,editableTask} from './app/taskSlice';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import 'react-datepicker/dist/react-datepicker-cssmodules.css';
import { useParams } from "react-router-dom";
import { selectTask } from './app/taskSlice';

/*страница добавления новой задачи */
function NewTask(props) {

  const navigate = useNavigate();
  const dispatch=useDispatch();
  

  /*const [startTaskDate, setStartTaskDate] = useState((paramFromApp.param == 'new') ? startTaskDate : new Date(task[Number(paramFromApp.param)].fromDate));
  const [endTaskDate, setEndTaskDate] = useState((paramFromApp.param == 'new') ? endTaskDate : new Date(task[Number(paramFromApp.param)].endDate));
  const [isDo , setIsDo] = useState((paramFromApp.param == 'new') ? false : task[Number(paramFromApp.param)].isDo);*/
  const [startTaskDate, setStartTaskDate] = useState(new Date());
  const [endTaskDate, setEndTaskDate] = useState(new Date());
  const [isDo , setIsDo] = useState(false);
  const task = useSelector(selectTask);

  let paramFromApp = useParams();/*номер задачи для редактирования */ 

   /*Переход на страницу Список задач по клику */
  function goToApp(){
    navigate(-1)
  }

  function sendObj(param){
    let textHead=document.getElementById('taskHeader').value;
    let textBody=document.getElementById('taskText').value;

    const objTask = {
      'header':textHead,
      'text': textBody,
      'fromDate': (param == 'new') ? startTaskDate : new Date(task[Number(param)].fromDate),
      'endDate':  (param == 'new') ? endTaskDate : new Date(task[Number(param)].endDate),
      'isDo': (param == 'new') ? false : task[Number(param)].isDo
    }

    return objTask;
  }
  
  /*добавление новой задачи */
  function addNewTask() {
     /*let textHead=document.getElementById('taskHeader').value;
     let textBody=document.getElementById('taskText').value;
     
     const objNewTask = {
      'header':textHead,
      'text': textBody,
      'fromDate': startTaskDate,
      'endDate': endTaskDate,
      'isDo': false
     }*/

     let objNewTask=sendObj(paramFromApp.param);
     dispatch(addTask(objNewTask));
     alert('Задача добавлена');

    }

    function editExistTask() {
      /*let textHead=document.getElementById('taskHeader').value;
      let textBody=document.getElementById('taskText').value;
     
     const objNewTask = {
      'header':textHead,
      'text': textBody,
      'fromDate': startTaskDate,
      'endDate': endTaskDate,
      'isDo': (paramFromApp.param == 'new') ? false : task[Number(paramFromApp.param)].isDo
     }*/

     let objEditTask=sendObj(paramFromApp.param); 
     dispatch(addTask(objEditTask));
     alert('Задача добавлена');
    }

    

    return (
      <div className="App">
      <header className="App-header">
       <div className='mainDiv'>
        <button className='goToButton marginNewTask' onClick={goToApp}>Назад к списку задач</button>
        <textarea id='taskHeader' className='TitleTaskTextarea marginNewTask' placeholder='Название задачи'>{(paramFromApp.param == 'new') ? '' : task[Number(paramFromApp.param)].header}</textarea>
        <textarea id='taskText' className='TextTaskTextarea marginNewTask' placeholder='Текст Задачи'>{(paramFromApp.param == 'new') ? '' : task[Number(paramFromApp.param)].text}</textarea>
        <div className='dateDiv addNewTask'>
          <div className='taskDate'>
            <span>Начало задачи</span>
            <DatePicker selected={(paramFromApp.param == 'new') ? startTaskDate : new Date(task[Number(paramFromApp.param)].fromDate)} onChange={(date) => setStartTaskDate(date)} />  
          </div>
          <div className='taskDate'>
            <span>Окончание задачи</span>
            <DatePicker selected={(paramFromApp.param == 'new') ? endTaskDate : new Date(task[Number(paramFromApp.param)].endDate)} onChange={(date) => setEndTaskDate(date)} /> 
          </div>
        </div>
        <button className='addNewTask' onClick={(paramFromApp.param == 'new') ? addNewTask : editExistTask}>Добавить/Изменить задачу</button>
       </div>
      </header>
    </div>
    )
  }
  
  export default NewTask;
